bot = Bot(token=TOKEN)

async def main():
    if len(sys.argv) < 2:
        print("‚ö†Ô∏è –û—à–∏–±–∫–∞: –Ω—É–∂–Ω–æ —É–∫–∞–∑–∞—Ç—å –∞—Ä–≥—É–º–µ–Ω—Ç 'morning', 'evening', 'push' –∏–ª–∏ 'fear_push'.")
        return

    mode = sys.argv[1].lower()

    # –í—ã–±–∏—Ä–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –ø–æ —Ä–µ–∂–∏–º—É
    if mode == 'morning':
        message = random.choice(MORNING_MESSAGES)
        need_photo = True
    elif mode == 'evening':
        message = random.choice(EVENING_MOTIVATIONS)
        need_photo = True
    elif mode == 'push':
        message = random.choice(MOTIVATIONAL_PUSHES)
        need_photo = True
    elif mode == 'fear_push':
        message = random.choice(FEAR_PUSHES)
        need_photo = False  # üö® –ù–ï–¢ –∫–∞—Ä—Ç–∏–Ω–∫–∏
    else:
        print("‚ö†Ô∏è –û—à–∏–±–∫–∞: –∞—Ä–≥—É–º–µ–Ω—Ç –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å 'morning', 'evening', 'push' –∏–ª–∏ 'fear_push'.")
        return

    # –¢–µ–≥–∞–µ–º –°—Ç–∞—Å–∞ (–ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è USERNAME)
    message_with_tag = f"{USERNAME} {message}"

    try:
        if need_photo:
            # –°–æ–±–∏—Ä–∞–µ–º —Å–ø–∏—Å–æ–∫ –∫–∞—Ä—Ç–∏–Ω–æ–∫
            images = [os.path.join(IMAGES_FOLDER, img) for img in os.listdir(IMAGES_FOLDER) if img.lower().endswith(('.jpg', '.jpeg', '.png'))]
            if not images:
                print("‚ö†Ô∏è –ù–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –≤ –ø–∞–ø–∫–µ.")
                return

            photo_path = random.choice(images)

            # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Ñ–æ—Ç–æ —Å –ø–æ–¥–ø–∏—Å—å—é
            with open(photo_path, 'rb') as photo:
                await bot.send_photo(
                    chat_id=CHAT_ID,
                    message_thread_id=THREAD_ID,
                    photo=photo,
                    caption=message_with_tag,
                    parse_mode='Markdown'
                )
            print(f"‚úÖ –°–æ–æ–±—â–µ–Ω–∏–µ ({mode}) –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ —Å —Ñ–æ—Ç–æ: {photo_path}")
        else:
            # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –¢–û–õ–¨–ö–û —Ç–µ–∫—Å—Ç
            await bot.send_message(
                chat_id=CHAT_ID,
                message_thread_id=THREAD_ID,
                text=message_with_tag,
                parse_mode='Markdown'
            )
            print(f"‚úÖ –°–æ–æ–±—â–µ–Ω–∏–µ ({mode}) –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ –ë–ï–ó —Ñ–æ—Ç–æ.")

    except Exception as e:
        print(f"‚ö†Ô∏è –û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ ({mode}): {e}")

if __name__ == '__main__':
    asyncio.run(main())

